src/lib/crafting-calculator.ts:
import type { ProjectType, CraftingOutcome } from "./types";

interface CraftingInput {
  project: {
    type: ProjectType;
    artifactRating: number;
    objectivesMet: number;
  };
  successes: number;
  targetNumber: number;
  isExceptional: boolean;
  intervalsRemaining?: number;
  legendaryBonusRoll?: number[];
}

const outcomes = {
  failure: {
    title: "An Unfortunate Setback",
    description:
      "Despite your best efforts, the materials refuse to cooperate. The project is marred by a critical flaw, requiring you to either start over or spend significant time correcting the mistake. A lesson in humility.",
  },
  success: {
    title: "A Masterful Creation",
    description:
      "Your skilled hands have worked their magic. The object is well-made, functional, and bears the hallmark of a true artisan. It will serve its purpose admirably.",
  },
  exceptionalSuccess: {
    title: "A Work of True Genius",
    description:
      "You have surpassed mere craftsmanship and touched upon the sublime. The final product is a thing of beauty and remarkable quality, exceeding all expectations. It may even possess minor, unintended beneficial properties.",
  },
  superiorSuccess: {
      title: "Echoes of the First Age",
      description: "The Essence of the Unconquered Sun flows through your work. The artifact hums with power, its form perfected, its function absolute. You have forged a legend."
  },
  legendarySuccess: {
      title: "A New Star in Heaven",
      description: "This is no mere object, but a myth made manifest. Its creation sends ripples through the fabric of Creation, a testament to a will that can forge reality itself. Its destiny is now intertwined with your own."
  }
};

export function calculateCraftingOutcome(
  input: CraftingInput,
): CraftingOutcome {
  const { project, successes, targetNumber, isExceptional } = input;
  const isSuccess = successes >= targetNumber;

  let sxp = 0;
  let gxp = 0;
  let wxp = 0;
  let outcomeTitle = "";
  let outcomeDescription = "";

  if (!isSuccess) {
    outcomeTitle = outcomes.failure.title;
    outcomeDescription = outcomes.failure.description;
  } else {
    // Determine title and description based on success level
    if (project.type.startsWith("legendary")) {
        outcomeTitle = outcomes.legendarySuccess.title;
        outcomeDescription = outcomes.legendarySuccess.description;
    } else if (project.type.startsWith("superior")) {
        outcomeTitle = outcomes.superiorSuccess.title;
        outcomeDescription = outcomes.superiorSuccess.description;
    } else if (isExceptional) {
        outcomeTitle = outcomes.exceptionalSuccess.title;
        outcomeDescription = outcomes.exceptionalSuccess.description;
    } else {
        outcomeTitle = outcomes.success.title;
        outcomeDescription = outcomes.success.description;
    }

    // Calculate XP based on project type
    switch (project.type) {
      case "basic-project":
        sxp = (isExceptional ? 3 : 2) * project.objectivesMet;
        break;
      case "basic-repair":
        sxp = 1 * project.objectivesMet;
        break;
      case "major-project":
        gxp = (isExceptional ? 3 : 2) * project.objectivesMet;
        sxp = 1 * project.objectivesMet;
        break;
      case "major-repair":
        gxp = 1 * project.objectivesMet;
        break;
      case "superior-project":
        if (project.objectivesMet >= 1) {
            const wxpMap: { [key: number]: number } = { 2: 3, 3: 5, 4: 7, 5: 9 };
            wxp = wxpMap[project.artifactRating] || 0;
        }
        // Assuming intervalsRemaining is passed for gxp calculation
        gxp = (project.artifactRating * 2) * (input.intervalsRemaining || 0);
        break;
      case "superior-repair":
         wxp = (project.artifactRating - 1);
        break;
      case "legendary-project":
        if (project.objectivesMet >= 1) {
            wxp = 10;
        }
        // Handle legendary bonus roll
        if (input.legendaryBonusRoll) {
            input.legendaryBonusRoll.forEach(die => {
                if (die >= 7) gxp++;
                else sxp++;
            });
        }
        break;
      case "legendary-repair":
        // No XP for legendary repair
        break;
    }
  }

  return {
    isSuccess,
    outcomeTitle,
    outcomeDescription,
    experienceGained: {
      sxp: Math.max(0, sxp),
      gxp: Math.max(0, gxp),
      wxp: Math.max(0, wxp),
    },
  };
}

src/lib/dice-logic.ts:
import type { Character, DiceRoll } from "./types";
import { allCharms } from "./charms";

const ANIMATION_DELAY = 100;

// --- Helper Functions ---

const rollDie = () => Math.floor(Math.random() * 10) + 1;

const shouldDieExplode = (roll: number, activeCharms: string[]): boolean => {
  if (activeCharms.includes("flawless-handiwork-method") && roll === 10) return true;
  if (activeCharms.includes('supreme-masterwork-focus-3') && roll >= 7) return true;
  if (activeCharms.includes('supreme-masterwork-focus-2') && roll >= 8) return true;
  if (activeCharms.includes('supreme-masterwork-focus-1') && roll >= 9) return true;
  return false;
};

const calculateSuccesses = (roll: number, activeCharms: string[]) => {
  const smf1 = activeCharms.includes('supreme-masterwork-focus-1');
  const smf2 = activeCharms.includes('supreme-masterwork-focus-2');
  const smf3 = activeCharms.includes('supreme-masterwork-focus-3');

  if (roll >= 10) return 2;
  // Note: These need to be checked from most to least powerful, as a higher charm implies the lower one is active.
  if (smf3 && roll >= 7) return 2; 
  if (smf2 && roll >= 8) return 2;
  if (smf1 && roll >= 9) return 2;
  if (roll >= 7) return 1;
  return 0;
};

// --- Main Logic Function ---

interface DiceRollInput {
  character: Character;
  activeCharms: string[];
  targetNumber: number;
  willpowerSpent: number;
  onProgress: (interimRoll: DiceRoll) => void;
}

export const performDiceRoll = async (input: DiceRollInput): Promise<DiceRoll> => {
    const { character, activeCharms, targetNumber, willpowerSpent, onProgress } = input;

    await new Promise(resolve => setTimeout(resolve, 50));

    const initialDicePool = character[character.selectedAttribute] + character.craft;
    const allRolls: number[][] = [];
    let totalSuccesses = 0;

    // --- Step 1: Initial Roll & Reroll ---
    let firstWaveRolls = Array.from({ length: initialDicePool }, rollDie);
    let diceToExplode: number[] = [];

    const willRerollFailures = activeCharms.includes("first-movement-of-the-demiurge");
    if (willRerollFailures) {
        // Show the pre-reroll dice for a moment
        allRolls.push([...firstWaveRolls]);
        onProgress({
            diceHistories: allRolls, totalSuccesses: 0, automaticSuccesses: 0, targetNumber, activeCharmNames: [], activeCharmIds: []
        });
        await new Promise(resolve => setTimeout(resolve, ANIMATION_DELAY + 200));

        // Reroll failures and update the first wave
        const rerolledWave = firstWaveRolls.map(die => (die < 7 ? rollDie() : die));
        allRolls[0] = rerolledWave; // Replace the first wave with the rerolled dice
        firstWaveRolls = rerolledWave;
    } else {
        allRolls.push(firstWaveRolls);
    }

    // Calculate successes and gather explosions for the first wave
    for (const die of firstWaveRolls) {
        totalSuccesses += calculateSuccesses(die, activeCharms);
        if (shouldDieExplode(die, activeCharms)) {
            diceToExplode.push(die);
        }
    }

    // Update progress after the first wave is fully processed
    onProgress({
        diceHistories: allRolls,
        totalSuccesses,
        automaticSuccesses: 0, 
        targetNumber,
        activeCharmNames: [],
        activeCharmIds: []
    });
    await new Promise(resolve => setTimeout(resolve, ANIMATION_DELAY));


    // --- Step 2: Explosion Loop (Waves) ---
    let diceForNextWave = diceToExplode.length;

    while (diceForNextWave > 0) {
        const currentWaveRolls = Array.from({ length: diceForNextWave }, rollDie);
        allRolls.push(currentWaveRolls);

        let explosionsInThisWave = 0;
        for (const die of currentWaveRolls) {
            totalSuccesses += calculateSuccesses(die, activeCharms);
            if (shouldDieExplode(die, activeCharms)) {
                explosionsInThisWave++;
            }
        }

        diceForNextWave = explosionsInThisWave;

        onProgress({
            diceHistories: allRolls,
            totalSuccesses,
            automaticSuccesses: 0,
            targetNumber,
            activeCharmNames: [],
            activeCharmIds: []
        });

        if (diceForNextWave > 0) {
            await new Promise(resolve => setTimeout(resolve, ANIMATION_DELAY));
        }
    }

    // --- Step 3: Final Calculation & Return ---
    let automaticSuccesses = 0;
    const activeCharmDetails = allCharms.flatMap(charm => {
        const charms = [];
        if (activeCharms.includes(charm.id)) charms.push(charm);
        if (charm.subEffects) {
            charm.subEffects.forEach(subCharm => {
                if (activeCharms.includes(subCharm.id)) charms.push(subCharm);
            });
        }
        return charms;
    });

    activeCharmDetails.forEach((charm) => {
        if (charm.id === 'will-forging-discipline') {
            automaticSuccesses += willpowerSpent * 2;
        }
        else if (charm.effect.type === "add_successes") {
            automaticSuccesses += charm.effect.value || 0;
        }
        else if (charm.effect.type === "add_successes_per_essence") {
            automaticSuccesses += character.essence * (charm.effect.value || 0);
        }
    });

    const finalTotalSuccesses = totalSuccesses + automaticSuccesses;

    const finalResult: DiceRoll = {
        diceHistories: allRolls,
        totalSuccesses: finalTotalSuccesses,
        automaticSuccesses,
        targetNumber: targetNumber,
        activeCharmNames: activeCharmDetails.filter(c => c.category === 'functional').map(c => c.name),
        activeCharmIds: activeCharms,
    };

    // Final progress update with all data
    onProgress(finalResult);

    return finalResult;
};

src/lib/types.ts:
export const ATTRIBUTES = [
  "intelligence",
  "wits",
  "perception",
  "strength",
  "dexterity",
  "stamina",
  "charisma",
  "manipulation",
  "appearance",
] as const;

export type Attribute = (typeof ATTRIBUTES)[number];

export const PROJECT_TYPES = [
    "basic-project",
    "major-project",
    "superior-project",
    "legendary-project",
    "basic-repair",
    "major-repair",
    "superior-repair",
    "legendary-repair",
  ] as const;

export type ProjectType = (typeof PROJECT_TYPES)[number];

export interface Project {
  type: ProjectType;
  artifactRating: number; // 0 for non-artifacts
  objectivesMet: number;
}

export interface ActiveProject {
  id: string;
  name: string;
  type: ProjectType;
  goal: number;
  progress: number;
  isComplete: boolean;
}

export interface CraftingExperience {
  sxp: number;
  gxp: number;
  wxp: number;
}


export interface Character {
  intelligence: number;
  wits: number;
  perception: number;
  strength: number;
  dexterity: number;
  stamina: number;
  charisma: number;
  manipulation: number;
  appearance: number;
  craft: number;
  essence: number;
  personalMotes: number;
  peripheralMotes: number;
  willpower: number;
  selectedAttribute: Attribute;
  knownCharms: string[]; // Array of charm IDs
}

export interface CharmEffect {
  type:
    | "add_successes"
    | "add_successes_per_essence"
    | "reroll_failures"
    | "reroll_tens"
    | "double_success"
    | "lower_repair_difficulty"
    | "custom";
  value?: number;
}

export interface Charm {
  id: string;
  name:string;
  cost?: string;
  description: string;
  minCraft: number;
  minEssence: number;
  effect: CharmEffect;
  category?: 'functional' | 'narrative';
  subEffects?: Charm[];
}

export interface DiceRoll {
  diceHistories: number[][];
  totalSuccesses: number;
  automaticSuccesses: number;
  targetNumber: number;
  activeCharmNames: string[];
  activeCharmIds: string[];
}

export interface CraftingOutcome {
  isSuccess: boolean;
  outcomeTitle: string;
  outcomeDescription: string;
  experienceGained: CraftingExperience;
}

